import { useState } from 'react';
export const useInput = initialValue => {
  const {
    0: value,
    1: setValue
  } = useState(initialValue);
  return {
    value,
    setValue,
    reset: () => setValue(initialValue),
    bind: {
      value,
      onChange: event => setValue(event.target.value)
    }
  };
};
